query PostsByCategory(
  $category: String
) {
  allMarkdownRemark(limit: 1000, sort: {fields: [fields___prefix], order: DESC}, filter: {frontmatter: {category: {eq: $category}}}) {
    totalCount
    edges {
      node {
        fields {
          slug
        }
        excerpt
        timeToRead
        frontmatter {
          title
          category
        }
      }
    }
  }
  site {
    siteMetadata {
      facebook {
        appId
      }
    }
  }
}

query PostBySlug(
  $slug: String!
) {
  post: markdownRemark(fields: {slug: {eq: $slug}}) {
    id
    html
    fields {
      slug
      prefix
    }
    frontmatter {
      title
      author
      category
      cover {
        childImageSharp {
          resize(width: 300) {
            src
          }
        }
      }
    }
  }
  authornote: markdownRemark(fileAbsolutePath: {regex: "/author/"}) {
    id
    html
  }
  site {
    siteMetadata {
      facebook {
        appId
      }
    }
  }
}

query PageByPath(
  $slug: String!
) {
  page: markdownRemark(fields: {slug: {eq: $slug}}) {
    id
    html
    frontmatter {
      title
    }
  }
  site {
    siteMetadata {
      facebook {
        appId
      }
    }
  }
  bgDesktop: imageSharp(fluid: {originalName: {regex: "/three/"}}) {
    resize(width: 1200, quality: 90, cropFocus: CENTER) {
      src
    }
  }
  bgTablet: imageSharp(fluid: {originalName: {regex: "/three/"}}) {
    resize(width: 800, height: 1100, quality: 90, cropFocus: CENTER) {
      src
    }
  }
  bgMobile: imageSharp(fluid: {originalName: {regex: "/three/"}}) {
    resize(width: 450, height: 850, quality: 90, cropFocus: CENTER) {
      src
    }
  }
}

query PostsQuery {
  posts: allMarkdownRemark(filter: {fileAbsolutePath: {regex: "//posts/[0-9]+.*--/"}}, sort: {fields: [fields___prefix], order: DESC}) {
    edges {
      node {
        excerpt
        fields {
          slug
          prefix
        }
        frontmatter {
          title
          category
          author
          cover {
            children {
              __typename
              ... on ImageSharp {
                fluid(maxWidth: 800, maxHeight: 360) {
                  ...GatsbyImageSharpFluid_withWebp
                }
              }
            }
          }
        }
      }
    }
  }
  site {
    siteMetadata {
      facebook {
        appId
      }
    }
  }
}

fragment GatsbyImageSharpFluid_withWebp on ImageSharpFluid {
  base64
  aspectRatio
  src
  srcSet
  srcWebp
  srcSetWebp
  sizes
}

