    edges {
      node {
        fields {
          slug
          prefix
        }
        frontmatter {
          title
          menuTitle
        }
      }
    }
  }
  footnote: markdownRemark(fileAbsolutePath: {regex: "/footnote/"}) {
    id
    html
  }
}
